def text_to_Chars(text):
    lines = []
    for char in text.upper():
        if char in Chars:
            for i in range(len(Chars[char])):
                if len(lines) - 1 < i:
                    lines.append([])
                lines[i].append(Chars[char][i])


    return "\n".join(["".join(line) for line in lines])

